import React, { useEffect } from "react";

const botTypeClasses = {
  Assault: "icon military",
  Defender: "icon shield",
  Support: "icon plus circle",
  Medic: "icon ambulance",
  Witch: "icon magic",
  Captain: "icon star",
};

function BotCard({ bot, onDelete, onAddToArmy }) {
  const handleAddToArmy = () => {
    onAddToArmy(bot);
  };
  useEffect(() => {
    fetch('http://localhost:8002/bots')
      .then(response => response.json())
      .then(data => setSelectedBots(data))
      .catch(error => console.error('Error fetching bots:', error));
  }, []);

  return (
    <div className="ui column">
      <div className="ui card" key={bot.id}>
        <div className="image">
          <img alt="oh no!" src={bot.avatar_url} />
        </div>
        <div className="content">
          <div className="header">
            {bot.name}
            <i className={botTypeClasses[bot.bot_class]} />
          </div>
          <div className="meta text-wrap">
            <small>{bot.catchphrase}</small>
          </div>
        </div>
        <div className="extra content">
          <span>
            <i className="icon heartbeat" />
            {bot.health}
          </span>
          <span>
            <i className="icon lightning" />
            {bot.damage}
          </span>
          <span>
            <i className="icon shield" />
            {bot.armor}
          </span>
          <span>
            <div className="ui center aligned segment basic">
              <button className="ui mini red button" onClick={onDelete}>
                x
              </button>
              <button
                className="ui mini green button"
                onClick={handleAddToArmy}
              >
                Add to Army
              </button>
            </div>
          </span>
        </div>
      </div>
    </div>
  );
}

export default BotCard;
