// YourBotArmy.js
import React, { useState, useCallback } from "react";
import BotCard from "./BotCard";

function YourBotArmy() {
  const [selectedBots, setSelectedBots] = useState([]);
  const [selectedBotDetails, setSelectedBotDetails] = useState(null);

  const addToBotArmy = useCallback(async (bot) => {
    setSelectedBots(prevBots => [...prevBots, bot]);

    // Fetch the details of the selected bot from the server
    try {
      const response = await fetch(`http://localhost:8002/bots/${bot.id}`);
      if (!response.ok) {
        throw new Error('Failed to fetch bot details');
      }
      const botDetails = await response.json();
      setSelectedBotDetails(botDetails);
    } catch (error) {
      console.error('Error fetching bot details:', error);
    }
  }, []);

  const removeFromBotArmy = (botId) => {
    setSelectedBots(prevBots => prevBots.filter(bot => bot.id !== botId));
    setSelectedBotDetails(null); // Clear the selected bot details when it is removed from the army
  };

  return (
    <div className="ui segment inverted olive bot-army">
      <div className="ui five column grid">
        <div className="row bot-army-row">
          Your Bot Army
        </div>
        <div className="row bot-army-row">
          {selectedBots.map(bot => (
            <BotCard
              key={bot.id}
              bot={bot}
              onDelete={() => removeFromBotArmy(bot.id)}
              onAddToArmy={addToBotArmy} // Make sure to pass the function here
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default YourBotArmy;
